mapper.select:
select email, pw from USER_INFO
where email=#{email} and
pw=#{pw}

mapper.update:
update USER_INFO
set
nick
=#{nick}, address =#{address}, phone =#{phone}
where
email=#{email}

update USER_INFO
set pw =
#{newPw}
where email = #{email} and pw =#{pw}

mapper.select:
select like_state from
LIKE_INFO
where email=#{email} b_id=#{b_id}

select email
from USER_INFO
where phone = #{phone}
or email =
#{email}
