mapper.select:
select email, pw, nick from USER_INFO
where email=#{email}
and
pw=#{pw}

mapper.update:
update USER_INFO
set
nick
=#{nick}, address =#{address}, phone =#{phone}
where
email=#{email}

update USER_INFO
set pw =
#{newPw}
where email = #{email} and pw =#{pw}

mapper.select:
select email
from USER_INFO
where phone = #{phone}
or email =
#{email}

select B_LIKE_STATE
FROM BOARD_LIKE_INFO
WHERE B_ID = #{b_id}
and EMAIL = #{email}
